package br.part1;

public class NumAndDen {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int numerador = 24;
		int denominador = 5;
		double wholeResult = numerador / denominador;
		double fractionalResult = numerador % denominador;
		System.out.println("o resultado é " + wholeResult + " e " + fractionalResult + "/" + denominador);
	}

}
